  public function handle()
  {

    /**
     * Оглавление
     *
     *  1. Получить коллекцию всех комнат чата
     *  2. Подчистить каждую комнату от устаревших и лишних сообщений
     *
     *  N. Вернуть статус 0
     *
     */

    //------------------------------------------------------//
    // Удалить истёкшие или лишние сообщения из комнат чата //
    //------------------------------------------------------//
    $res = call_user_func(function() { try { DB::beginTransaction();

      // 1. Получить коллекцию всех комнат чата
      $rooms = \M10\Models\MD1_rooms::query()->get();

      // 2. Подчистить каждую комнату от устаревших и лишних сообщений
      foreach($rooms as $room) {

        // 2.1. Кусками по 100 штук запрашивать сообщения комнаты $room
        // - Если с момента создания $message прошло более $room->expire минут
        // - То удалить $message
        \M10\Models\MD2_messages::query()->whereHas('rooms', function($query) USE ($room) {
          $query->where('name', $room->name);
        })->chunk(100, function($messages) USE ($room) {

          foreach($messages as $message) {

            if(gmp_cmp(\Carbon\Carbon::parse($message->created_at)->diffInMinutes(), $room->expire) > 0) {
              $message->rooms()->detach();
              $message->m5_users()->detach();
              $message->delete();
            }

          }

        });

        // 2.2. Если кол-во сообщений в $room больше messages_limit, удалить лишние из начала

          // 1] Получить кол-во сообщений в комнате $room
          $count = \M10\Models\MD2_messages::query()->whereHas('rooms', function($query) USE ($room) {
            $query->where('name', $room->name);
          })->count();

          // 2] Сравнить $count и messages_limit
          if(gmp_cmp($count, $room->messages_limit) > 0) {

            // 2.1] Извлечь лишние сообщения
            $messages = \M10\Models\MD2_messages::query()->whereHas('rooms', function($query) USE ($room) {
              $query->where('name', $room->name);
            })->orderBy('created_at', 'desc')
              ->skip($room->messages_limit)
              ->take(+$count-$room->messages_limit)
              ->get();

            // 2.2] Удалить лишние сообщения
            foreach($messages as $message) {
              $message->rooms()->detach();
              $message->m5_users()->detach();
              $message->delete();
            }

          }

      }


    DB::commit(); } catch(\Exception $e) {
        $errortext = 'Invoking of command C5_clear_expired_or_extra_messages from M-package M10 have ended on line "'.$e->getLine().'" on file "'.$e->getFile().'" with error: '.$e->getMessage();
        DB::rollback();
        Log::info($errortext);
        write2log($errortext, ['M10', 'C5_clear_expired_or_extra_messages']);
        return [
          "status"  => -2,
          "data"    => [
            "errortext" => $errortext,
            "errormsg" => $e->getMessage()
          ]
        ];
    }}); if(!empty($res)) return $res;


    //---------------------//
    // N. Вернуть статус 0 //
    //---------------------//
    return [
      "status"  => 0,
      "data"    => ""
    ];

  }

