  public function handle()
  {

    /**
     * Оглавление
     *
     *  1. Получить и проверить входящие данные
     *  2. Получить трейд с tradeid и tradeofferid
     *  3. Сменить статус трейда на another_status_id
     *  4. Сделать commit
     *  5. Обновить кэш
     *  6. Вернуть в магазин ранее заблокированные предметы отменяемого трейда
     *
     *  N. Вернуть статус 0
     *
     */

    //---------------------------------//
    // Cancel the active trade db part //
    //---------------------------------//
    $res = call_user_func(function() { try { DB::beginTransaction();

      // 1. Получить и проверить входящие данные
      $validator = r4_validate($this->data, [

        "tradeid"           => ["required", "regex:/^[1-9]+[0-9]*$/ui"],
        "tradeofferid"      => ["required", "regex:/^[1-9]+[0-9]*$/ui"],
        "another_status_id" => ["required", "regex:/^[1-9]+[0-9]*$/ui"],
        "id_user"           => ["required", "regex:/^[1-9]+[0-9]*$/ui"],

      ]); if($validator['status'] == -1) {

        throw new \Exception($validator['data']);

      }

      // 2. Получить трейд с tradeid и tradeofferid
      $trade = \M14\Models\MD4_trades::query()
          ->where('id', $this->data['tradeid'])
          ->where('tradeofferid', $this->data['tradeofferid'])
          ->first();
      if(empty($trade))
        throw new \Exception('Не удалось найти трейд в m14.md4_trades по id = '.$this->data['tradeid']);

      // 3. Сменить статус трейда на another_status_id
      $trade->id_status = $this->data["another_status_id"];
      $trade->save();

      // 4. Сделать commit
      DB::commit();

      // 5. Обновить кэш
      $result = runcommand('\M14\Commands\C7_update_cache', [
        "all" => false,
        "cache2update" => [
          "m14:processor:trades:status:2"
        ]
      ]);
      if($result['status'] != 0)
        throw new \Exception($result['data']['errormsg']);

      // 6. Вернуть в магазин ранее заблокированные предметы отменяемого трейда
      $result = runcommand('\M14\Commands\C17_add_goods', [
        "id_trade"    => $trade['id']
      ]);
      if($result['status'] != 0)
        throw new \Exception($result['data']['errormsg']);

    DB::commit(); } catch(\Exception $e) {
        $errortext = 'Invoking of command C12_cancel_the_active_trade_dbpart from M-package M14 have ended on line "'.$e->getLine().'" on file "'.$e->getFile().'" with error: '.$e->getMessage();
        DB::rollback();
        Log::info($errortext);
        write2log($errortext, ['M14', 'C12_cancel_the_active_trade_dbpart']);
        return [
          "status"  => -2,
          "data"    => [
            "errortext" => $errortext,
            "errormsg" => $e->getMessage()
          ]
        ];
    }}); if(!empty($res)) return $res;


    //---------------------//
    // N. Вернуть статус 0 //
    //---------------------//
    return [
      "status"  => 0,
      "data"    => ""
    ];

  }

